float layer = 35.;
float pop = .4;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    vec4 final;
    for( float i = 0.; i <= layer; i ++ ){
        float zoom = (1.+(1.-i/layer)*pop);
        uv = (uv-.5)*zoom + .5;
        uv += vec2(iMouse.x/iResolution.x-.5,.0)*(i/layer-.5)*0.2;

        vec4 tex = texture(iChannel0,uv);
        float value = (tex.r+tex.g+tex.b)/3. * layer;
        if ( round(value) == i ){
            final += round(value)/layer *step(uv.x,1.)*step(uv.y,1.)*step(.0,uv.x)*step(.0,uv.y);
        }
        uv = fragCoord/iResolution.xy;
    }

    fragColor = final;
}
