vec2 fix(vec2 vc, vec2 aspect) {
    vc *= aspect;
    vc -= aspect * 0.5;
    vc /= 0.25;

    return vc;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 aspect = vec2(iResolution.x / iResolution.y, 1.0);

    vec2 uv = fragCoord / iResolution.xy;
    uv = fix(uv, aspect);

    vec2 mouse = iMouse.xy / iResolution.xy;
    mouse = fix(mouse, aspect);

    float smth = 0.001; // smoothness
    float scl = 1.0; // scale

    vec3 col = vec3(0.0);
    vec3 bgCol = vec3(0.0);
    vec3 mainCol = vec3(1.0);

    if (!( floor(length(uv)) < floor(length(mouse)) )) {
        mainCol = vec3(1., 0., 0.);
        scl /= 0.95;
    }

    float c = length(uv * scl);
    float b = smoothstep(c - smth, c + smth, 1.0);
    col = vec3(b);

    col = mix(bgCol, mainCol, col);

    // cursor
    col = mix(vec3(1.0), col, floor(length((uv - mouse) / 0.05)));

    fragColor = vec4(col,1.0);
}
