void mainImage( out vec4 fragColor, in vec2 fragCoord ) {    
    float u = fragCoord.x/iResolution.x;//normalizacja wyswietlania dla osi x
    float v = fragCoord.y/iResolution.y;//normalizacja wyswietlania dla osi y
    vec4 rog = vec4(.0,.0,.0,1.0);		// definicja koloru rogow
	vec4 kc = vec4(.8,0.,0.,1.);		// definicja koloru czerwonego
    vec4 kz = vec4(0.,.8,0.,1.);		// definicja koloru zielonego
    vec4 kb = vec4(1.,1.,1.,1.);		// definicja koloru bialego
    vec4 cz = vec4(.0,.0,.0,1.);		// definicja koloru czarnego
    if (mod(iTime,2.) < 1.0) {rog = kb;}// reszta z dzielenia biezacego czasu skladowej itime definuje kolor rogow jezeli jest wieksza od 1 kolor rogu jest bialy
	if (mod(iTime,2.) > 1.0) {rog = cz;}// reszta z dzielenia biezacego czasu skladowej itime definuje kolor rogow jezeli jest mniejsza od 1 kolor rogu jest czarny
    /*
	W nastepnej czesci dzielimy ekran na 30 czesci i definiujemy ich kolory 
	poprzez wykorzystanie funkcji mod, skladowej wektrou idate podajacej 
	czas w sekundach oraz zmiennych zefinowanych wczesniej i odwolania do ich skladowej
	*/
    if( u > 0.0 && v > 0.8) {fragColor = rog;} //lewy gorny róg
    if( u > 0.2 && v > 0.8) {fragColor = vec4(mod(iDate.w, .2),mod(iDate.w,.4),.4,1);}
    if( u > 0.4 && v > 0.8) {fragColor = vec4(mod(iDate.w, .2),mod(iDate.w,.5),.6,1);}
    if( u > 0.6 && v > 0.8) {fragColor = vec4(mod(iDate.w, .4),mod(iDate.w,.6),.8,1);}
    if( u > 0.8 && v > 0.8) {fragColor = rog;} //prawy górny róg
    if( u > 0.0 && v < 0.8) {fragColor = vec4(mod(iDate.w, .6),.2,mod(iDate.w,.6),1);}
    if( u > 0.2 && v < 0.8) {fragColor = vec4(mod(iDate.w, .7),.4,mod(iDate.w,.7),1);}
    if( u > 0.4 && v < 0.8) {fragColor = vec4(mod(iDate.w, .8),.6,mod(iDate.w,.8),1);}
    if( u > 0.6 && v < 0.8) {fragColor = vec4(mod(iDate.w, .9),.8,mod(iDate.w,.9),1);}
    if( u > 0.8 && v < 0.8) {fragColor = vec4(mod(iDate.w, .8),1.,mod(iDate.w,.2),1);}
    if( u > 0.0 && v < 0.6) {fragColor = vec4(.1,mod(iDate.w,.4),mod(iDate.w,.4),1);}
    if( u > 0.2 && v < 0.6) {fragColor = vec4(.2,mod(iDate.w,.8),mod(iDate.w,.6),1);}
    if( u > 0.4 && v < 0.6) {fragColor = rog;} //środek
    if( u > 0.6 && v < 0.6) {fragColor = vec4(.4,mod(iDate.w,.4),mod(iDate.w,.4),1);}
    if( u > 0.8 && v < 0.6) {fragColor = vec4(.5,mod(iDate.w,.6),mod(iDate.w,.6),1);}
    if( u > 0.0 && v < 0.4) {fragColor = vec4(1.,.9,mod(iDate.w,.2),1);}
    if( u > 0.2 && v < 0.4) {fragColor = vec4(kc.xy,mod(iDate.w,.4),1);}
    if( u > 0.4 && v < 0.4) {fragColor = vec4(kz.xy,mod(iDate.w,.6),1);}
    if( u > 0.6 && v < 0.4) {fragColor = vec4(kc.xy,mod(iDate.w,.8),1);}
    if( u > 0.8 && v < 0.4) {fragColor = vec4(kz.xy,mod(iDate.w,.4),1);}
    if( u > 0.0 && v < 0.2) {fragColor = rog;} //lewy dolny róg
    if( u > 0.2 && v < 0.2) {fragColor = vec4(mod(iDate.w, .6),kc.xy,1);}
    if( u > 0.4 && v < 0.2) {fragColor = vec4(mod(iDate.w, .7),kz.xy,1);}
    if( u > 0.6 && v < 0.2) {fragColor = vec4(mod(iDate.w, .8),kc.xy,1);}
    if( u > 0.8 && v < 0.2) {fragColor = rog;} //prawy dolny róg

}
