#define PI 3.14156
#define PixelSize 18. / iResolution.y
#define R iResolution.xy
#define I2 mat2(1,0,0,1)
#define t (0.5*cos(iTime+PI)+0.5)

mat2 mat2interpolate(mat2 a, mat2 b) {
	return mat2(a[0][0]*(1.-t) + b[0][0] * t,
                a[0][1]*(1.-t) + b[0][1] * t,
                a[1][0]*(1.-t) + b[1][0] * t,
                a[1][1]*(1.-t) + b[1][1] * t);    
}

float grid(vec2 p) {
    vec2 f = smoothstep(PixelSize, 0., abs(fract(4.*p)-PixelSize));
    return clamp(f.x + f.y, 0., 1.);
}

float T1(vec2 p) {
    mat2 T = mat2(-1,0,0,-1);
    p *= mat2interpolate(I2, T);
    return grid(p);
}

float T2(vec2 p) {
    mat2 T = mat2(.5, 0, 0, .5);
    p *= mat2interpolate(I2, T);
    return grid(p);
}

float T3(vec2 p) {
    mat2 T = mat2(1,.75,0,1);
    p *= mat2interpolate(I2, T);
    return grid(p);
}

float T4(vec2 p) {
    mat2 T = mat2(1,0,-.75,-1);
    p *= mat2interpolate(I2, T);
    return grid(p);
}

float T5(vec2 p) {
    mat2 T = mat2(.25, .5, .75, .1);
    p *= mat2interpolate(I2, T);
    return grid(p);
}

float T6(vec2 p) {
    float c = cos(iTime), s = sin(iTime);
    mat2 T = mat2(c,s,-s,c);
    p *= mat2interpolate(I2, T);
    return grid(p);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/R.y;
    uv *= 2.;
    vec2 uvp = trunc(uv);
    vec2 uvf = 2.*fract(uv)-1.;
    vec3 col = vec3(0);
    if(uvp.x == 0. && uvp.y == 0.) col = mix(col, vec3(1), T1(uvf));
    if(uvp.x == 1. && uvp.y == 0.) col = mix(col, vec3(1), T2(uvf));
    if(uvp.x == 0. && uvp.y == 1.) col = mix(col, vec3(1), T3(uvf));
    if(uvp.x == 1. && uvp.y == 1.) col = mix(col, vec3(1), T4(uvf));
    if(uvp.x == 2. && uvp.y == 0.) col = mix(col, vec3(1), T5(uvf));
    if(uvp.x == 2. && uvp.y == 1.) col = mix(col, vec3(1), T6(uvf));
    if(uvp.x > 2.) col = vec3(0);
    fragColor = vec4(col,1.0);
}
