#define M_PI 3.1415926535897932384626433832795

float plot(vec2 st, float pct){
  return  smoothstep( pct-0.01, pct, st.y) -
          smoothstep( pct, pct+0.01, st.y);
}

float plot_y(vec2 st, float pct, float w){
  return  smoothstep( pct-w, pct, st.y) -
          smoothstep( pct, pct+w, st.y);
}

float plot_x(vec2 st, float pct, float w){
  return  smoothstep( pct-w, pct, st.x) -
          smoothstep( pct, pct+w, st.x);
}

float spot(vec2 p, float r) {
  float d = length(p);
  return smoothstep(0.,d,r);
}


vec3 bgcolor(vec2 uv) {
  float m1 = 0.;
  m1 = clamp(.0, 0.5, uv.y*uv.y );
  return vec3(0.,0., m1);
}

vec3 sun(vec2 uv) {
  float t = fract(iTime*0.03);

  vec2 p = uv;
  p.y -= (t*0.4 - .0625);
  vec3 basecol = vec3(0.0,0.0,0.05) ;
  basecol.r += smoothstep( 0.28, 1.5, t*3.4)*0.80 ;
  basecol.g += smoothstep( 0.32, 1.5, t*3.2)*0.75 ;
  basecol.b += smoothstep( 0.38, 1.5, t*3.2)*0.80 ;
  vec3 col=basecol;
  vec2 m = vec2(0.01,0.01);
  return col*smoothstep(0.1, -0.1 , length(p) )*smoothstep(-0.03,0.01,uv.y);
}

vec3 sky(vec2 uv) {
  float t = fract(iTime*0.03);
  //t *=0.90;
  vec3 basecol = vec3(0.0,0.0,0.05) ;
  basecol.r += smoothstep( 0.30, 1.2, t*2.8 )*0.79 ;
  basecol.g += smoothstep( 0.34, 1.2, t*2.6 )*0.80 ;
  basecol.b += smoothstep( 0.40, 1.2, t*2.5 )*0.95 ;
  vec3 col=basecol;
  return col*smoothstep(-0.2, 0.1 ,uv.y )*smoothstep(-0.4, 0.005,uv.y);
}

vec3 ocean(vec2 uv, vec3 basecol) {
  vec3 col=basecol;
  //*smoothstep(-1.,0.,uv.y);
  return col*step(uv.y, 0.) ;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
  vec2 uv = fragCoord.xy / iResolution.xy ;
  uv -= .5;
  float asp = iResolution.x/iResolution.y;
  uv.x *= asp;

  vec3 col =  vec3(0.);
  col += bgcolor(uv);
  vec3 cl = vec3(0,0,0.18);
  col = ocean(uv, cl) ;
  col += sky(uv) ;
  col += sun(uv) ;

  fragColor = vec4(col, 1.) ;
}
