void mainImage(out vec4 o, in vec2 U)
{
    #define t iTime
	vec2 r = iResolution.xy, e = vec2(1e-4,-1e-2);
	U = (U - .5 * r) / max(r.x, r.y);
	vec3 O = vec3(0,0,1), d = vec3(fract(3.*U)-.5, -1.), c,N;
	#define f(p) dot(vec3(0,0,1),p-pow(10.*max(abs(p.x),abs(p.y)),4.))
	float h = .2;
    h+=(f((O+d*h))+f((O+d*h)));
    h*=(fract(2.*h)-.5);
	h *= (fract(2.*h)-.5);
	vec3 l = normalize(vec3(-2, 2, 2));
	l.xy=cos(t)*l.xy+sin(t)*vec2(l.y, -l.x);
	l.zx=cos(t)*l.zx+sin(t)*vec2(l.x, -l.z);
	O +=d *h;
	N=normalize(f(O)-vec3(f((O-e.xyy)),f((O-e.yxy)),f((O-e.yyx))));
	c = vec3(abs(U.y)<.2?dot(l,N):.8);
	o= vec4(sqrt(c*dot(O,O)),1);
}

float sing(float t,int ch){
  int it=int(t);
  float f=1e-6,a=f,s=f,tm=t,c=.15+.15*cos(t);
  float p0=float[](.4,.45,.5)[it%3],p1=float[](.4,.36,.45)[it%3];
  float[]cw =float[](0.,0.,0.,0.);
  cw[0]=p0;cw[1]=2.;cw[2]=3.;cw[3]=5.+p1;
  for(int i=0;i<4;i++){
    s=abs(.05-cos(6.2831*40.*cw[i]*tm));
    t*=cw[i];
    a+=.11+.11*sin(t);
    vec2 p=vec2(1.-a,asin(a));
    f=(f+sin(cos(2.337*t)+6.2831*60.*t))*max(c,1.-dot(p,p));
  }
  tm=fract(tm);
  s=(ch==1?.5-sin(floor(12.*(1.-.5*tm))*f)/(3.-2.*cos(s))*(tm*sqrt(1.-tm)):1e-5);
  return 4.*f*(tm*exp(log2(1.-tm)/(1.+s)));
}
