void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float row = floor(fract(-iChannelTime[0] / 4.286) * iResolution.y);
    if (fragCoord.y > row && fragCoord.y < row + 1.0)
    {
        vec2 uv = fragCoord/iResolution.xy;
		float fft  = texture(iChannel0, vec2(uv.x,0.25)).x;
        fragColor = vec4(vec3(sin(iTime+fft*8.0+uv.xyx+vec3(0.5,1.5,7))), 1.0);
    }
    else
    {
        discard;
    }
}
