float PIX=8.0;
float PT=2.0;
float PV=0.5;
float PC=1.0;
float A=0.0;
float B=0.0;
float C=0.0;   

#define BW vec3(0.5,0.5,0.5)
#define CU vec3(A,B,C)

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // normalized pixel coordinates
    vec2 p = PIX*fragCoord/iResolution.yx;

    // pattern
    float f = sin(p.x + sin(PT*p.y + iTime)) +
              sin(length(p)+iTime) +
              PV*sin(p.x*2.5+iTime);

    // color
    vec3 col = PC * sin(f + BW * CU);

    // putput to screen
    fragColor = vec4(col,1.0);
}
