float skew = 1.73;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
   	// middle & squared 
    vec2 uv = (fragCoord - .5 * iResolution.xy)/iResolution.y;

    vec3 col = vec3(0);
    // vertical mirror
    uv = abs(uv);

    // skew 45

    float c = dot(uv,normalize(vec2(1,skew)));


    // vertical sides
    c = max(c, uv.x);


    // width
    col += step(c,0.3);


    // Output to screen
    fragColor = vec4(col,1.0);
}
