void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (2. * fragCoord - iResolution.xy) / min(iResolution.x, iResolution.y);
    vec3 base = vec3(0., iTime * 2., 0.);
    vec3 pos = mod(base + vec3(uv, 2.) + vec3(.5), 10.) - vec3(5.), dir = normalize(vec3(uv, 2.));

    const int Steps = 50;
    int i;
    float mind = 1e30;
    float di = 0.;
    for (i = 0; i < Steps; i++) {
    	float d = length(pos - vec3(0., 0., 5.)) - 1.4;
        di = di + step(d, 0.001);
        mind = min(mind, d);
        pos = mod(pos + d * dir + vec3(5.), 10.) - vec3(5.);
    }

    di /= float(Steps);

    fragColor = vec4(vec2(di) * vec2(1. - mind), 1., 1.) * (di);
}
