void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec4 box = iMouse.z > 0. ?
             vec4(min(iMouse.xy, iMouse.zw),
                  max(iMouse.xy, iMouse.zw)) / iResolution.xyxy :
             vec4(0., 0., 1., 1.),
         shiftedBox = mix(vec2(2.4, 0.0).xyxy, vec2(4.0, 1.0).xyxy, box);
    vec2 uv = fragCoord / iResolution.xy,
         shiftedUV = mix(shiftedBox.xy, shiftedBox.zw, uv);
    float r = shiftedUV.x,
          y = shiftedUV.y,
          y_ = y;

    fragColor = vec4(1);
    float closest = 1.;
    for (int i = 0; i < 1000; i++) {
        y_ = r * y_ * (1.0 - y_);
        if (i > 750) closest = min(closest, abs(y - y_));
    }
    fragColor.rgb *= clamp(0., 1., closest * 500.);
}
