void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 xy = fragCoord / iResolution.xy;
    vec4 col = vec4(1.0,1.0,1.0,1.0);
    if (xy.x > 0.125){
        col.r = 1.0;
        col.g = 1.0;
        col.b = 0.0;
    }
    if (xy.x > 0.25){
        col.r = 0.0;
        col.g = 1.0;
        col.b = 1.0;
    }
    if (xy.x > 0.375){
        col.r = 0.0;
        col.g = 1.0;
        col.b = 0.0;
    }
    if (xy.x > 0.5){
        col.r = 1.0;
        col.g = 0.0;
        col.b = 1.0;
    }
    if (xy.x > 0.625){
        col.r = 1.0;
        col.g = 0.0;
        col.b = 0.0;
    }
    if (xy.x > 0.75){
        col.r = 0.0;
        col.g = 0.0;
        col.b = 1.0;
    }
    if (xy.x > 0.875){
        col.r = 0.0;
        col.g = 0.0;
        col.b = 0.0;
    }
    fragColor = col;
}
