float skew = 1.73;
float blur = 0.01;
float scale = 0.001;
float HexDist(vec2 p) {
    // vertical mirror
	p = abs(p);

    // hex skew;
    float c = dot(p, normalize(vec2(1,skew)));
    // vertical sides
    c = max(c, p.x);

    return c;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
   	// middle & squared 
    vec2 uv = (fragCoord - .5 * iResolution.xy)/iResolution.y;

    vec3 col = vec3(0);

    // Hex Gradient
    //col += HexDist(uv);

    // Animated Hex Gradient
    float speed = 2.0;
    col += 0.8 * sin(HexDist(uv) * 80.+iTime * speed);


    // width
    col += smoothstep(scale+blur,scale,HexDist(uv));


    // Output to screen
    fragColor = vec4(col,1.);
}
