int lehmerSeed=0;

void srandomLehmer(vec2 uv)
{
 int a=int(uv.x)<<16;
 int b=int(uv.y);
 lehmerSeed=a^b;
}

float randomLehmer()
{
 int tmp=0;
 tmp=lehmerSeed*1234567890;
 tmp=(tmp>>16)^tmp;
 tmp=tmp*1234567890;
 return float((tmp>>16)^tmp)/pow(2.,31.);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    float col=0.;

    uv+=iTime*.2;
    vec2 zoomedUv=uv*20.;

    vec2 uvFloor=floor(zoomedUv);
    vec2 uvFract=fract(zoomedUv);
    vec2 inter=smoothstep(0.,1.,uvFract);

    srandomLehmer(uvFloor+vec2(+0,+0));
    float x0y0=randomLehmer();

    srandomLehmer(uvFloor+vec2(+1,+0));
    float x1y0=randomLehmer();

    srandomLehmer(uvFloor+vec2(+0,+1));
    float x0y1=randomLehmer();

    srandomLehmer(uvFloor+vec2(+1,+1));
    float x1y1=randomLehmer();

    float y0=mix(x0y0,x1y0,inter.x);
    float y1=mix(x0y1,x1y1,inter.x);
    col=+mix(y0,y1,inter.y);

    // Output to screen
    fragColor = vec4(col,0.0,0.0,1.0);
}
