Main {
    Neighborhood;
   	vec3 no = normalize(vec3(e.x-w.x,n.x-s.x,2));
	Q = abs(sin(atan(A(U).w*.1+D(U)*vec4(1,.1,0,0))));
    Q *= 0.7+0.3*texture(iChannel1,no);
    Q.xyz *= 1.8*mat3(.7,1,.8,.7,.1,0,.9,.6,0.8);
	//Q = 10.*abs(A(U));
}

Main {
	Neighborhood;

    vec4 dd = D(U);
    Q = A(U);
    // Gradient of Pressure
    Q.xy += 0.25*vec2(w.z-e.z,s.z-n.z);
    // Curl of Torsion
    Q.xy += 0.25*vec2(s.w-n.w,e.w-w.w);
    vec2 f = vec2(0);
    for (int x = -1; x <= 1; x++)
    for (int y = -1; y <= 1; y++) if (x!=0||y!=0) {
    	vec4 d = D(U+vec2(x,y)); 
        f += Q.w*d.w*(A(U+vec2(x,y)).xy-Q.xy);
    }
    Q.xy -= f/30.*dd.x;
    float 
        u = length(D(U+vec2(0,1)).yz),
        r = length(D(U+vec2(1,0)).yz),
        d = length(D(U-vec2(0,1)).yz),
        l = length(D(U-vec2(1,0)).yz);

}

Main {
	Neighborhood;
    Q = A(U);
    vec4 dd = D(U);
    // convergence of velocity
    Q.z += 0.25*((w.x-Q.x)-(e.x-Q.x)+(s.y-Q.y)-(n.y-Q.y));
    // curl of velocity
    Q.w += ((e.y-Q.y)-(w.y-Q.y)+(s.x-Q.x)-(n.x-Q.x));
    if (dd.y<.1) Q.w *= 0.;
    if (dd.y<1e-4) Q.z *= 0.;
    if (R.x-U.x<1.) Q.xy*=0.;

}

Main {
	Neighborhood
    Q = A(U-0.25*(n+e+s+w).xy);

        if (iMouse.z>0. && length(U-iMouse.xy)<10.||(iFrame < 1 &&length(U-0.5*R)<20.))
        {Q.x = 1.;Q.z += 1e-2;}
}

vec4 T (vec2 U) {
	return D(U-A(U).xy);
}
Main {
    Neighborhood;
    Q = T(U);
    vec4 a = A(U),
        u = T(U+vec2(0,1)),
        r = T(U+vec2(1,0)),
        d = T(U-vec2(0,1)),
        l = T(U-vec2(1,0));
    Q -= 0.25*(u*(n.y-a.y)-d*(s.y-a.y)+r*(e.x-a.x)-l*(w.x-a.x));
    vec4 M = (0.25*(u+r+d+l));
   	Q.y = M.x;
    if (iMouse.z>0. && length(U-iMouse.xy)<10.||(iFrame < 1 &&length(U-0.5*R)<20.)) 
        Q.xz = vec2(1,.5);

}
