#define time iTime

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    float winkingSpeed = 0.74;
    float winking = abs(sin(time*winkingSpeed))+0.2;

    float glow = 0.08 * winking; //Brightness.

    float segmentation = 1408. ; //Change this value to alternate pattern. Higher values act strange.

    float flowSpeed = 1.; //Alternate vertical flow.

	float nc = tan(.147 -uv.x * segmentation ) * abs(sin(uv.y*time*flowSpeed)) * sin(uv.y*segmentation) * glow;

    vec3 col = vec3(nc, nc*.6, nc*.35);
    fragColor = vec4(col,1.0);
}
