#define R iResolution.xy
#define A(U) texture(iChannel0,(U)/R)
vec4 X (inout vec2 c, vec2 u, vec2 r) {
    vec4 n = A(u+r);
	if (length(u+r-n.xy)<length(u-c)) c = n.xy;
    return n;
} 
void mainImage( out vec4 Q, vec2 U )
{
    vec4 a = A(U);
    vec2 c = Q.xy;
    vec4 
        n = X(c,U,+vec2(0,1)),
        e = X(c,U,+vec2(1,0)),
        s = X(c,U,-vec2(0,1)),
        w = X(c,U,-vec2(1,0)),
        m = 0.25*(n+e+s+w);
 	vec3 g = normalize(vec3(e.z-w.z,n.z-s.z,.3));
    g = reflect(g,vec3(0,0,1));
 	vec3 b = normalize(vec3(e.w-w.w,n.w-s.w,1));
    float d = dot(g,normalize(vec3(0,1,.5)));
    Q = (exp(-4.*d*d))*m.w*abs(sin(2.+vec4(1,2,3,4)*(1.+2.*m.z)));

    Q = .8+.2*g.x-.8*(1.+0.5*(b.x+b.y)) * a.w * sin(2.+0.5*(g.z)*vec4(1,2,3,4));
}

#define R iResolution.xy
#define A(U) texture(iChannel0,(U)/R)
vec4 X (inout vec2 c,inout float m, vec2 u, vec2 r) {
    vec4 n = A(u+r);
    m += n.z;
	if (length(u+r-n.xy)<length(u-c)) c = n.xy;
    return n;
} 
void mainImage( out vec4 Q, vec2 U )
{
    Q = A(U);
    vec2 c = Q.xy;
    float m=0.;
    vec4 
        n = X(c,m,U,+vec2(0,1)),
        e = X(c,m,U,+vec2(1,0)),
        s = X(c,m,U,-vec2(0,1)),
        w = X(c,m,U,-vec2(1,0));
    X(c,m,U,vec2(1,1));
    X(c,m,U,vec2(1,-1));
    X(c,m,U,vec2(-1,1));
    X(c,m,U,vec2(-1,-1));
    vec2 g = vec2(e.z-w.z,n.z-s.z);
    Q.xy = c;
    Q.z += (m/8.-Q).z+.05*Q.w - .0001*Q.z;
    Q.w -= 0.001*Q.w;
    Q.zw = max(Q.zw,vec2(2,1)*smoothstep(4.,0.0,length(U-c)));
    Q.xy -= 0.25*g;
    if (length(U-iMouse.xy)<.3*R.y&&iMouse.z>0.) Q = vec4(-R,Q.z,0);
    if (iFrame < 1) Q = vec4(clamp(floor(U/2.)*2.,0.5*R-2.,0.5*R+2.),0,0);

}

