void mainImage( out vec4 Q, in vec2 U )
{
    Q = 1.-abs(sin(atan(.03*A(U))*(1.+.3*vec4(1,2,3,4))));
}

float ln (vec2 a, vec2 b, vec2 u) {
	u = u-a;
    b = b-a;
    return length(u-b*clamp(dot(u,b)/dot(b,b),0.,1.));
}
void mainImage( out vec4 Q, in vec2 U )
{
    Q = A(U);
    float m, dm, p, dp, n, dn, r, dr, g, dg, b, db;
    if (iFrame < 1) {
    	m = 0.5;
    	p = -0.9;
    	n = 0.4;
    	r = -0.7;
    	g = 0.4;
    	b = -0.55;
        dm = 0.;
        dn = 0.;
        dp = 0.;
        dr = 0.;
        dg = 0.;
        db = 0.;
    } else {
    	m = (A(vec2(5,0)).w);
    	p = A(vec2(5,1)).w;
    	n = A(vec2(5,2)).w;
    	r = A(vec2(5,3)).w;
    	g = A(vec2(5,4)).w;
    	b = A(vec2(5,5)).w;
        dm = A(vec2(5,6)).w;
        dn = A(vec2(5,7)).w;
        dp = A(vec2(5,8)).w;
        dr = A(vec2(5,9)).w;
        dg = A(vec2(5,10)).w; 
        db = A(vec2(5,11)).w;
    }
    #define dt .001
    vec2 u = 2.2*(U-vec2(.5)*R)/R.y;
    vec2 A = vec2(m+p+n,r+g+b), B;
    for (int i = 0; i < 100; i++) {
            float mag = sqrt(m*m+n*n+p*p+r*r+g*g+b*b);
            float np = sqrt(n*n+p*p);
            float rgb = sqrt(r*r+g*g+b*b);
			//vec2 A = vec2(dm+dp+dn,dr+dg+db);

            dm += dt*(m/mag - sign(m));
            dn += dt*(n/mag - n/np) ;
            dp += dt*(p/mag - p/np) ;
            dr += dt*(r/mag - r/rgb) ;
            dg += dt*(g/mag - g/rgb) ;
            db += dt*(b/mag - b/rgb) ;

            m += dt*dm;
            n += dt*dn;
            p += dt*dp;
            r += dt*dr;
            g += dt*dg;
            b += dt*db;

        	B = vec2(m+p+n,r+g+b);

        Q.xyz += exp(-2e2*ln(A,B,u));
        A = B;
    }
         if (U.y < 1.) Q.w = m;
    else if (U.y < 2.) Q.w = p;
    else if (U.y < 3.) Q.w = n;
    else if (U.y < 4.) Q.w = r;
    else if (U.y < 5.) Q.w = g;
    else if (U.y < 6.) Q.w = b;
    else if (U.y < 7.) Q.w = dm;
    else if (U.y < 8.) Q.w = dp;
    else if (U.y < 9.) Q.w = dn;
    else if (U.y <10.) Q.w = dr;
    else if (U.y <11.) Q.w = dg;
    else if (U.y <12.) Q.w = db;
   Q.xyz *= .9995;
}
