float N21(vec2 p) {
    p = fract(p*vec2(234.234, 823.923));
    p += dot(p, p+42.34);
    return fract(p.x*p.y);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;
    vec2 UV = fragCoord.xy/iResolution.xy;
    vec2 UV2 = abs(UV-0.5);

    uv.x += iTime*0.1;
    uv.y += sin(iTime)*0.1+iTime*0.05;

    vec3 col = vec3(1.0, UV.x, sin(0.5*iTime+UV.y)*0.5+0.5);

    uv *= 7.;
    vec2 gv = fract(uv)-.5;
    vec2 id = floor(uv);

    float n = N21(id);
    float band = -.03+pow(0.5*UV.y, 1.5);
    float width = 0.01+0.1*UV.x;

    if (n > 0.5) gv.x *= -1.;

    float d = length(gv - .5*sign(gv.x+gv.y+.001))-0.5;
    d = abs(d);
    d = abs(d-band);
    d = abs(d-band);
    d = abs(d-band); 
    d = abs(d)-width;

    float fuzzy = 0.01+0.2*pow(UV2.x+UV2.y, 2.);
    float mask = smoothstep(fuzzy, 0.0, d);

    col *= mask;

    fragColor = vec4(col,1.0);
}
