vec3 getColor(float n)
{
    return vec3((1. - n - 0.5) * 1.3, n * (1. - n) * 1.3, (n - 0.5) * 1.3);

    return vec3((n - 0.25) * 1.3, 0., 0.);
    return vec3((1. - n - 0.25) * 1.3, 0., 0.);
    return vec3(n * (1. - n) * 1.3, 0., 0.);
    return vec3(1. - n, 0., 0.);
    return vec3(n * 2. + 3., n * 2. - 1., n);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    vec3 col = getColor(uv.x);
  	fragColor = vec4(pow(col,vec3(1./2.2)), 1);
}
