float random (vec2 st) {
    return fract(sin(dot(st.xy, vec2(12.9898,78.233))) * 43758.5453123);
}

const float LOW_LUMA = .3;
const float HIGH_LUMA = .7;

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    float time = floor(iTime);
    vec2 uv = fragCoord / iResolution.xy;
    vec2 mirrorUv = vec2(1. - uv.x, uv.y);
    vec3 cameraColor = texture(iChannel0, mirrorUv).rgb;
    float luma = cameraColor.r; // maybe should be more sophisticated
    vec3 color;
    if (luma < LOW_LUMA || luma > HIGH_LUMA) {
        float index = mod(fragCoord.y, 8.);
        if (index < 2.) {
            color = vec3(0.);
        } else {
            color = vec3(
                random(fragCoord.xy + time),
                random(fragCoord.xy + 100. + time),
                random(fragCoord.xy + 200. + time)
            );
        }
    } else {
        color = cameraColor;
    }
    fragColor = vec4(color, 1.0);
}
