void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    vec2 tuv = uv;
    uv.y /= iResolution.x/iResolution.y;
    vec3 col = vec3(1.0);
    //vec2 origin = vec2(0.5, 0.28);
    vec2 origin = iMouse.xy/iResolution.xy;
    origin.y /= iResolution.x/iResolution.y;

    float dist = distance(origin, uv);

    vec3 ylwcol = vec3(1.0, 0.85, 0.0);
    vec3 whtcol = vec3(1.0, 1.0, 1.0);
    vec3 prpcol = vec3(0.5, 0.0, 0.6);
    vec3 blkcol = vec3(0.05, 0.05, 0.05);

    float ylw = step(dist, 0.07);
    float wht = step(dist, 0.12) - ylw;
    float prp = step(dist, 0.17) - wht - ylw;
    float blk = step(dist, 0.22) - prp - wht - ylw;


    col = blkcol * blk;
    col += prpcol * prp;
    col += whtcol * wht;
    col += ylwcol * ylw;

    float inv = 1.0 - blk - prp - wht - ylw;
    float blkstrp = step(tuv.y, 0.25);
    float prpstrp = step(tuv.y, 0.5) - blkstrp;
    float whtstrp = step(tuv.y, 0.75) - prpstrp - blkstrp;
    float ylwstrp = 1.0 - whtstrp - prpstrp - blkstrp;

    vec3 bgflag = blkstrp * blkcol;
    bgflag += prpstrp * prpcol;
    bgflag += whtstrp * whtcol;
    bgflag += ylwstrp * ylwcol;

    col += inv * bgflag;

    fragColor = vec4(col,1.0);
}
