const int KEY_UP = 38;
const int KEY_DOWN  = 40;

vec4 B(vec2 pos)
{
   return SAMPLE(iChannel1, pos, size);
}

//density and velocity
vec3 pdensity(vec2 pos)
{
   vec4 particle_param = SAMPLE(iChannel0, pos, size);
   return vec3(particle_param.zw,gauss(pos - particle_param.xy, radius));
}

void mainImage( out vec4 fragColor, in vec2 pos )
{
   vec3 density = pdensity(pos);
   vec4 blur = SAMPLE(iChannel1, pos, size);
    float vorticity = B(pos+vec2(1,0)).y-B(pos-vec2(1,0)).y-B(pos+vec2(0,1)).x+B(pos-vec2(0,1)).x;
   //fragColor = vec4(SAMPLE(iChannel2, pos, size).xyz  + 0.8*vec3(0.4,0.6,0.9)*vorticity,1.0);
    if(texelFetch( iChannel2, ivec2(KEY_UP,2), 0 ).x > 0.5)
    {
        fragColor = vec4(7.*abs(density.xyy)*density.z,1.0);
        //fragColor = vec4(10.*abs(density.xyy) + 30.*vec3(0,0,abs(blur.z)),1.0);
    }
    else
    {
        fragColor = SAMPLE(iChannel3, pos, size);
    }  
}

//voronoi particle tracking 

void Check(inout vec4 U, vec2 pos, vec2 dx)
{
    vec4 Unb = SAMPLE(iChannel0, pos+dx, size);
    vec2 rpos1 = mod(pos-Unb.xy+size*0.5,size) - size*0.5;
    vec2 rpos2 = mod(pos-U.xy+size*0.5,size) - size*0.5;
    //check if the stored neighbouring particle is closer to this position 
    if(length(rpos1) < length(rpos2))
    {
        U = Unb; //copy the particle info
    }
}

vec4 B(vec2 pos)
{
   return 5.*SAMPLE(iChannel1, pos, size);
}

void mainImage( out vec4 U, in vec2 pos )
{
    U = SAMPLE(iChannel0, pos, size);

    //check neighbours 
    Check(U, pos, vec2(-1,0));
    Check(U, pos, vec2(1,0));
    Check(U, pos, vec2(0,-1));
    Check(U, pos, vec2(0,1));
    Check(U, pos, vec2(-1,-1));
    Check(U, pos, vec2(1,1));
    Check(U, pos, vec2(1,-1));
    Check(U, pos, vec2(1,-1));
    Check(U, pos, vec2(-8,0));
    Check(U, pos, vec2(8,0));
    Check(U, pos, vec2(0,-8));
    Check(U, pos, vec2(0,8));
    Check(U, pos, vec2(-32,0));
    Check(U, pos, vec2(32,0));
    Check(U, pos, vec2(0,-32));
    Check(U, pos, vec2(0,32));
    U.xy = mod(U.xy,size); //limit the position to the texture

    //make new particles by diverging existing ones
    float dist = length(mod(pos-U.xy+size*0.5,size) - size*0.5);
 	if(dist > 1./minimal_density)
    {
        float interp = clamp(divergibility*(dist-1./minimal_density),0.,1.);
        U.xy = U.xy*(1.-interp) + interp*pos;
    }

    vec2 ppos = U.xy;

    vec2 pressure = vec2(B(ppos+vec2(1,0)).z - B(ppos+vec2(-1,0)).z, B(ppos+vec2(0,1)).z - B(ppos+vec2(0,-1)).z);
    //mouse interaction
    if(iMouse.z>0.)
    {
        float k = gauss(ppos-iMouse.xy, 20.);
        U.zw = U.zw*(1.-k) + k*0.1*vec2(cos(0.02*iTime*dt), sin(0.02*iTime*dt));
    }

    #ifdef BLASTER
    float phi = 0.03*iTime*dt;
    // U.zw += 0.0001*vec2(cos(phi), sin(phi))*gauss(ppos-size*vec2(0.3,0.5),20.)*dt;
     phi = 0.03*iTime*dt+PI;
   //  U.zw += 0.0001*vec2(cos(phi), sin(phi))*gauss(ppos-size*vec2(0.7,0.5),20.)*dt;
    #endif

    //update the particle
    U.zw = U.zw*0.9999; // decrease velocity with time
    vec3 mass = SAMPLE(iChannel2, pos, size).xyz;
    U.zw += clamp(P*pressure*dt,-0.1,0.1) + dot(mass,mass)*vec2(0,-.0002)*pos.y/size.y;

    //border conditions
    if(ppos.x < 25. || size.x - ppos.x < 25.)
    {
       U.z *= 0.9;
    }
    if(ppos.y < 25. || size.y - ppos.y < 25.)
    {
       U.w *= 0.9;
    }

    U.xy += dt*U.zw;
    U.xy = mod(U.xy,size); //limit the position to the texture


    if(iFrame < 1)
    {
      	if(mod(pos, vec2(1./particle_density)).x < 1. && mod(pos, vec2(1./particle_density)).y < 1.)
           U = vec4(pos,0.,0.);

    }
}

#define size iResolution.xy
#define SAMPLE(a, p, s) texture((a), (p)/s)

float gauss(vec2 x, float r)
{
    return exp(-pow(length(x)/r,2.));
}
#define SPEED
#define BLASTER

#define PI 3.14159265

#ifdef SPEED
//high speed
    #define dt 10.0
    #define P 0.01
#else
//high precision
 	#define dt 2.
    #define P 0.15
#endif

//how many particles per pixel, 1 is max
#define particle_density 0.5
#define minimal_density 0.46

const float radius = 2.0;
//interpolate between particle and texture advection
const float particality = 0.05;
//should be small for particle advection (like 0.001 small)
const float divergibility = 1.;

vec4 B(vec2 pos)
{
   return SAMPLE(iChannel1, pos, size);
}

//density and velocity
vec3 pdensity(vec2 pos)
{
   vec4 particle_param = SAMPLE(iChannel0, pos, size);
   return vec3(particle_param.zw,gauss(pos - particle_param.xy, 0.7*radius));
}

const vec2 damp = vec2(0.000,0.01);
const vec2 ampl = vec2(0.1,1.);

void mainImage( out vec4 u, in vec2 pos )
{
    vec4 prev_u = SAMPLE(iChannel1, pos, size);


    vec3 density = pdensity(pos);
     //exponential rolling average
      u.xyz =  0.5*density;
    float div = B(pos+vec2(1,0)).x-B(pos-vec2(1,0)).x+B(pos+vec2(0,1)).y-B(pos-vec2(0,1)).y;
    u.zw = (1.-0.001)*0.25*(B(pos+vec2(0,1))+B(pos+vec2(1,0))+B(pos-vec2(0,1))+B(pos-vec2(1,0))).zw;
    u.zw += ampl*vec2(div,density.z);
}

vec4 B(vec2 pos)
{
   return SAMPLE(iChannel1, pos, size);
}

//density and velocity
vec3 pdensity(vec2 pos)
{
   vec4 particle_param = SAMPLE(iChannel0, pos, size);
   return vec3(particle_param.zw,gauss(pos - particle_param.xy, 0.7*radius));
}

const vec2 damp = vec2(0.000,0.01);
const vec2 ampl = vec2(0.1,1.);

void mainImage( out vec4 u, in vec2 pos )
{
    vec4 prev_u = SAMPLE(iChannel1, pos, size);


    vec3 density = pdensity(pos);
     //exponential rolling average
      u.xyz = 0.5*density;
    float div = B(pos+vec2(1,0)).x-B(pos-vec2(1,0)).x+B(pos+vec2(0,1)).y-B(pos-vec2(0,1)).y;
    u.zw = (1.-0.001)*0.25*(B(pos+vec2(0,1))+B(pos+vec2(1,0))+B(pos-vec2(0,1))+B(pos-vec2(1,0))).zw;
    u.zw += ampl*vec2(div,density.z);
}

//texture advection
void mainImage( out vec4 fragColor, in vec2 pos )
{
    vec4 particle = SAMPLE(iChannel0, pos, size);
    vec4 cur = SAMPLE(iChannel1, pos, size);

    fragColor =  SAMPLE(iChannel1, particality*particle.xy+(1.-particality)*(pos.xy-particle.zw*dt), size)*0.9985;

    #ifdef BLASTER
    float phi = 0.0004*float(iFrame)*dt;
    fragColor.xyz += 0.00*abs(vec3(sin(phi), sin(phi+2.*PI*0.66),sin(phi+4.*PI*0.66)))*gauss(pos-size*vec2(0.5,0.7),40.)*dt;
    phi = 0.0004*float(iFrame)*dt+PI*0.5;
    fragColor.xyz += 0.005*abs(vec3(sin(phi), sin(phi+2.*PI*0.66),sin(phi+4.*PI*0.66)))*gauss(pos-size*vec2(0.7,0.7),40.)*dt;
    #endif

    if(iMouse.z>0.)
    {
        float alpha = iTime*dt;
        float k = gauss(pos-iMouse.xy, 20.);
        fragColor.xyz = fragColor.xyz*(1.-k) + k*abs(vec3(sin(alpha), sin(alpha+2.*PI*0.66),sin(alpha+4.*PI*0.66)));
    }
    if(iFrame < 1)
    {
        fragColor.xyz = vec3(0.);
        fragColor.w = 1.;
    }
}
