void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
	vec2 Newuv;
    vec2 MyCen = iMouse.xy/iResolution.xy;//vec2(0.5,0.5);
    vec3 Color;
    float BlurDist = 0.05;
    float MyDist = distance(MyCen,uv);
    float BlurStart = 0.15;
   	if (MyDist>BlurStart)
    {
    	MyDist -= BlurStart;
        vec2 XVec = vec2(BlurDist*MyDist,0);
    	vec2 YVec = vec2(0,BlurDist*MyDist);    
    	vec3 BigDev = vec3(0.25);
    	Newuv = uv.xy + XVec;
    	Color += texture(iChannel0,Newuv).xyz*BigDev;
    	Newuv = uv.xy - XVec;
    	Color += texture(iChannel0,Newuv).xyz*BigDev;        
    	Newuv = uv.xy + YVec;
    	Color += texture(iChannel0,Newuv).xyz*BigDev;        
    	Newuv = uv.xy - YVec;
    	Color += texture(iChannel0,Newuv).xyz*BigDev;
    }
    else
    {
        Color += texture(iChannel0,uv).xyz;
    }   
    fragColor = vec4(Color,1.);

}
