// video getter
vec4 tex1(in vec2 st) { return texture(iChannel0, st); }
vec4 tex2(in vec2 st) { return texture(iChannel1, st); }

// transition
// ratio - value to select video, 0~1
// st - uv coordination of the pixel.
vec4 transition(float ratio, in vec2 st) {
    vec2 p = st*2.-1.;
    float l = pow(length(p),0.5)/sqrt(2.0);
    float ll = smoothstep(l-0.04, l+0.04, ratio);

    float w = .3;
    vec2 p1 = p*(1.+smoothstep(l-w, l+w, ratio));
    vec2 p2 = p*smoothstep(l-w, l+w, ratio);

    vec2 uv1 = p1*0.5+0.5;
    vec2 uv2 = p2*0.5+0.5;

    return mix(tex1(uv1), tex2(uv2), ll);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    float ratio = smoothstep(-1., 1., sin(iTime));
    vec4 video = transition(ratio, uv);

    vec3 col = uv.y<0.04 ? vec3(step(uv.x, ratio)) : video.rgb;

    fragColor = vec4(col,1.0);
}
