vec3 Blue = vec3(0.0,0.0,.9);
vec3 Red = vec3(.9,0.0,0.0);
vec3 White = vec3(1.0, 1.0, 1.0);
vec3 Black = vec3(0.0,0.0,0.0);
vec3 Grey=vec3(.3,.4,.5);
vec3 BombAnimation(vec2 uv,float sides, float y, float z,float posx,float posy)
{	
    float ratio=iResolution.x/iResolution.y;
    uv.x*=ratio;

    vec2 shapePos=(vec2(posx*ratio,posy));
    float dist=distance(shapePos,uv)*2.;
   	uv -= shapePos;
    float angle=atan(uv.y,uv.x);
    float radius = clamp(cos(z*angle)*sin(angle*y),-y,-z);
    radius = abs(cos(angle*sides));
    radius = abs(cos(angle*12.)*sin(angle*3.))*.8+.1;
    radius = smoothstep(-.5,1., cos(angle*10.))*0.2+0.5;
 	float value = smoothstep(dist-.10, dist*1.5+sin(iTime), radius/2.);
   	vec3 color = vec3(value);
    color += value * vec3(0.4, 0., 0.) * vec3(0.4,0.1,0.0);
    color += mix(vec3(.5,.2,.0),vec3(0.4,.1,.0),color);
    return color; 

}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
	vec3 image=BombAnimation(uv,12.,3.,8.,.5,.5);
   	//image=mix(Blue,Red,image);
    // Output to screen
    fragColor = vec4(image, 1.0);
}
