void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    uv -= 0.5;

    uv.y *= iResolution.y/iResolution.x;


    // Time varying pixel color
    float f1 = sin(atan(uv.y,uv.x)*25.+sin(length(uv)*10.-iTime*1.5)*40. + sin(length(uv)*250.-iTime*1.5)*0.25 + iTime*5. + sin(iTime));
    float f2 = sin(atan(uv.y,uv.x)*25.+sin(length(uv)*10.-iTime*1.5)*40. + sin(length(uv)*250.-iTime*1.5+5.)*0.25 + iTime*5. + sin(iTime+1.));
    float f3 = sin(atan(uv.y,uv.x)*25.+sin(length(uv)*10.-iTime*1.5)*40. + sin(length(uv)*250.-iTime*1.5+10.)*0.25 + iTime*5. + sin(iTime+2.));

    vec3 col = vec3(f1,f2,f3);

    // Output to screen
    fragColor = vec4(col,1.0);
}
