#define keyToggle(a)  ( texelFetch(iChannel3,ivec2(a,2),0).x > 0.) // https://www.shadertoy.com/view/llySRh
#define hash(p)         fract(sin( (p) * 178.233 ) * 43758.5453)   // https://www.shadertoy.com/view/llySRh

void mainImage( out vec4 O, vec2 u )
{
    int i = int(3.*hash(iTime)),
        j = int(2.*hash(iTime+1.));
    if (!keyToggle(32)) i = iFrame%3, j = 1;

    O = texture(iChannel0, u/iResolution.xy );

    O  = vec4( O[i],O[(i+1+j)%3], O[(i+2-j)%3], 1 );   // grey from colors
 // O *= vec4( (iFrame+ivec4(0,1,2,3))%3 - 1 );        // variant: colors from 3 monochromes
}
