float woosh(float x) {
    return x * x * x * (x * (x * 6. - 15.) + 10.); // "better smoothstep" from wikipedia
}

float smoothfold(float x) {
    return woosh(abs(fract(x) - 0.5) * 2.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    float loopLength = 4.0;

    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord - 0.5 * (iResolution.xy);
    float t = fract(iTime / loopLength);

    // Time varying pixel color
    float dist = log(dot(uv, uv));
    float angle = atan(uv.y, uv.x) / 6.28318530718;
    float v = dist*(smoothfold(t) + 1.0) + 2.0 * angle + 5.0 * smoothfold(t + 0.05 * dist);
    vec3 col = vec3(smoothfold(v), smoothfold(v+0.15), smoothfold(v+0.3));

    // Output to screen
    fragColor = vec4(col,1.0);
}
