// variant of https://shadertoy.com/view/td3yRN

void mainImage( out vec4 O, vec2 U )
{	O = fract(T(U)); }

#define hash(p) fract(sin(dot(p, vec2(12.9898, 78.233))) * 43758.5453)

                              //  +D : too regular
#define diff(s)  if ( hash(U+float(i)/11.+iTime+.3*float(s)) < .1 ) \
                    O[s] =  max(O[s], T(U+D)[s] )

void mainImage( out vec4 O, vec2 U )
{
    O = T( U );
    for (int i=0; i<9; i+= i==3 ? 2 : 1 ) {
        vec2 D = vec2( i%3-1, i/3-1);
        diff(0); diff(1); diff(2);
    }

    if (iFrame%1==0) O += .99* vec4( length(U-iResolution.xy/2.) < 2. );
}
