void mainImage(out vec4 o, in vec2 i) {

    o = vec4(0. < texelFetch(iChannel0, ivec2(39, 0), 0).x && 0. < texelFetch(iChannel0, ivec2(39, 1), 0).x ? 1 : 0,
             0. < texelFetch(iChannel0, ivec2(38, 0), 0).x && 0. < texelFetch(iChannel0, ivec2(38, 1), 0).x ? 1 : 0,
             0, 1);

    o += texture(iChannel0, vec2(i / iResolution.xy));
}
