#define N 300.0

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy * 2.0 - 1.0;
    uv.x *= iResolution.x/iResolution.y;

    vec3 col = vec3(0);
    for(int i = 0; i < int(N); i++) {
        float ii = float(i) / (N - 1.0);
        vec3 pos = vec3(
            0.0,
            (ii * 2.0 - 1.0) * 0.9,
            cos(ii*200.0 + iTime) / 2.0 + 1.0
        );
        pos.x = (pos.y - 1.0) * sin(ii*200.0 + iTime);
        float d = length(pos.xy - uv);
        col[i % 3] += smoothstep(-102.0, -20.0, 1.0/d - pos.z*200.0);;
    }

    // Output to screen
    fragColor = vec4(col,1.0);
}
