void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    float balanco= (sin(iTime+uv.y*1.5)/ 10.0);

    // Output to screen
    if(uv.x<0.3+balanco)
    {
        fragColor = vec4(1,0,0,1) * 1. - balanco * 2.;
    }else if(uv.x<0.68+balanco){
        fragColor = vec4(1,1,0,0) * 1. - balanco * 2.;
    }else
        fragColor = vec4(0,0.7,0,1) * 1. - balanco * 2.;
}
