void mainImage( out vec4 fragColor, in vec2 fragCoord )
{    
    //Get mouse positions.
	float mouseX = iMouse.x ;
    float mouseY = iMouse.y ;

    // can get colors with sin cos tan by sending 
    fragColor = vec4(abs(sin(mouseX)), abs(cos(mouseY)), abs(tan(mouseY)),0.0);
}
