#ifdef GL_FRAGMENT_PRECISION_HIGH
precision highp float;
#else
precision mediump float;
#endif



float line = 0.1;

float smin( float d1, float d2)
{
	float k = 0.6521; k = 1.1;
	float h = clamp( 0.5+0.5*(d2-d1)/k, 0.0, 1.0 );
	return mix( d2, d1, h ) - k*h*(1.0-h);
}

float f(vec2 uv)
{
	float x = uv.x;
	float y = uv.y;
    float f1 = cos((iTime + x)) - y;
    float f2 = sin(iTime + x)*abs(cos(iTime)) - y;
    //return smin(f1, f2);
    return f1;
}

vec2 df( in vec2 x )

{
	vec2 h = vec2( 0.01, 0.001 );
	return vec2( f(x+h.xy) - f(x-h.xy), f(x+h.yx) - f(x-h.yx) )/(2.0*h.x);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

	vec2 uv = fragCoord/iResolution.xy * vec2(10.0f, 10.0f);
    uv += vec2(0.0, -4.0);
	float col = float (abs(f(uv))/length(df(uv)));
	col = abs(f(uv))/sqrt(1.0 + pow(df(uv).x, 2.0));
	fragColor = vec4(smoothstep(0.98* line, line, col));
}
