void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    uv.x*=iResolution.x/iResolution.y;

    // THIS USES SCREEN SPACE NOT WORLD SPACE


    float ZOOMOUT = 1.;
    float SPEED = 1.;

    uv=ZOOMOUT*uv;

    float myTime=iTime*0.5+0.1*sin(iTime)*cos(2.2*iTime);
    //I dont experience time like a mortal

    myTime=SPEED * myTime;

    float lowManipulation= mod((2.*3.1415*uv.x*0.2-4.*myTime +sin(myTime)*3.*3.1415*uv.y*0.1+0.3*sin(uv.y) )*0.3,2.*3.1415);

    float highManipulation= (((sin(lowManipulation)*cos(7.*lowManipulation))+1.)/2.);

    fragColor = vec4( highManipulation , 0. , highManipulation ,1.0);
}
