void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 pos = fragCoord.xy;
    vec4 solidColor = vec4(0.0, 0.0, 0.0, 1.0);

    if(pos.x > 256.0 && pos.y < 144.0)
    {
        solidColor.r = 1.0;   
    }
    if(pos.x > 256.0 && pos.y > 144.0)
    {
        solidColor.g = 0.8;
    }
    if(pos.x < 256.0 && pos.y > 144.0)
    {
        solidColor.b = 0.8;
    }
    fragColor = solidColor;
}
