void mainImage( out vec4 fragColor, in vec2 fragCoord ){
	vec2 st = fragCoord/iResolution.xy;
    float pct = 0.0;

    //pct = step(distance(st,vec2(0.5)), (sin(iTime*4.)/4.)+0.25);
    //pct = smoothstep(((sin(iTime*4.))/4.)+.25, ((sin(iTime*4.))/2.)+.5,dot(st-0.5,st-0.5)*5.0);
    pct = smoothstep((sin( sin( sin( iTime * 0.5) * 3.3 ) * 3.8 ) / 2.5)+.5, 1.2, distance( st,vec2(0.5) ));
    //pct = smoothstep(((sin(iTime*4.))/4.)+.25, ((sin(iTime*4.))/2.)+.5,distance(st,vec2(0.5)));

    vec3 color = vec3(1.-pct, sin(iTime*4.)/20.+0.9, 0.9);

	fragColor = vec4( color, 1.0 );
}
