void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv= (fragCoord*2.0 -iResolution.xy) / iResolution.y  ;

    vec4 backColor = vec4(0.2,0.0,0.2,1.0);


    vec2 circleCenter = vec2(0.0,0.0);

    circleCenter = circleCenter + vec2(0.8,0.5);

    float radius = 0.25;

    float dist = distance(circleCenter,uv);

    float circle = smoothstep(dist,dist+0.01,radius);


    vec4 outColor = vec4(0.0);

    float bloomDist = dist -0.2;
    float bloom = clamp((1.0 - bloomDist),0.0,1.0)/3.0;
    vec4 bloomColor = vec4(1.0) + bloom;

    float sinus1 = sin(uv.x*1.1 +iTime*0.2) * 0.1 - 0.2;
    float land1 = smoothstep(sinus1,sinus1+0.01 ,uv.y);
    vec4 landColor1 = vec4(0.0, 0.4, 0.3, 1.0);

    float sinus2 = sin(uv.x*2.0 +iTime*0.95) * 0.2 - 0.45;
    float land2 = smoothstep(sinus2,sinus2+0.01, uv.y);
    vec4 landColor2 = vec4(0.0, 0.5, 0.3, 1.0);

    float sinus3 = sin(uv.x*5.0 +iTime*5.0) * 0.15 - 0.7;
    float land3 = smoothstep(sinus3,sinus3+0.01, uv.y);
    vec4 landColor3 = vec4(0.0, 0.55, 0.25, 1.0);







    outColor = mix(outColor, backColor, 1.0);
    outColor = mix(outColor, landColor1, 1.0 - land1);
    outColor = mix(outColor, landColor2, 1.0 - land2);
    outColor = mix(outColor, landColor3, 1.0 - land3);
    outColor = mix(outColor, bloomColor,bloom*1.5);
    outColor = mix(outColor,vec4(1.0), circle);




    fragColor = vec4(outColor );


}
