void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // - Normalize Coordinate -
    // fragCoord   : window relative coordinate
    // iResolution : window resolution in pixel

    //vec2 uv = fragCoord/iResolution.xy;
    vec2 uv = -(2. * fragCoord - iResolution.xy) / iResolution.y;

    // - Set BG Color -
    vec3 col = vec3(0.);

    // - Set the Gradient -
    vec2 pos = uv + vec2( 0., 0.);		// position

    col = vec3(length(pos));			// distance from position
    //col = vec3(1.-length(pos));

    // - Output -
    fragColor = vec4(col,1.0);
}
