void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/(vec2(360,360));


    vec3 color = vec3(0,0,0);

    for (int i=0; i < 4; i++){
        float delaydTime = iTime - float(i);
        float Circx = ((uv.x-0.88)-(cos(delaydTime*1.25))*0.6)*((uv.x-0.88)-(cos(delaydTime*1.25))*0.6);
        float Circy = ((uv.y-0.5)-(sin(delaydTime*2.5))*0.3)*((uv.y-0.5)-(sin(delaydTime*2.5))*0.3);

        if ((Circx + Circy) - 0.001 < 0.01){
            color = 0.5 + 0.5*sin(iTime+vec3(0,2,4));
        }
    }

    fragColor = vec4(color,1.0);
}
