void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;


    //Create a "percentage-based" split screen effect of two colors. Basically, create a variable called "Screen1WidthPercent" 
    //which is a float between 0-1. This decides how much space (percentage of screen width) the first window should take. 
    //The remaining should be colored in the alternate color.

    float Screen1WidthPercent = .4;

    if(uv.x <= Screen1WidthPercent)
        fragColor = texture(iChannel0, uv);
    else
        fragColor = texture(iChannel1, uv);


    //Draw horizontal red line at y = 50 (in pixel units)

    if(fragCoord.y == 50.5)
        fragColor = vec4(1.0,0.0,0.0,1.0);


    //In class, you learned about Samplers, try brightening the whole image by 10%.

    float brightness = 1.1;

    fragColor *= brightness;

}
