float Hash21(vec2 p){
    p = fract(p*vec2(234.34, 435.345));
    p += dot(p, p+34.23);
    return fract(p.x*p.y);
} 

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;
	vec2 UV = fragCoord.xy/iResolution.xy;
    vec3 col = vec3(0);

    uv += iTime*.04; //hace que se mueva
    uv *= 4.5;   //size 
    vec2 gv = fract(uv) -.5; //takes the fractional component of my uv 
    vec2 id = floor(uv);

    float n = Hash21(id);

    float width = .45 * UV.y;

    if(n < .5) gv.x *= -1.;//random fippling

    float d = abs(abs(gv.x + gv.y) -.5);
    vec2 cUv = gv - sign(gv.x + gv.y+0.001)*.5;
    d = length(cUv); // si lo descoment es circulos
    float mask = smoothstep(.01, -.01, abs(d-.5)-width);
    float angle = atan(cUv.x, cUv.y); // -Pi and +Pi
    float checker = mod(id.x + id.y, 2.)*2. -1.; // checker pattern 
    float flow = sin(iTime+checker*angle*12.);                   


    float x = (checker*angle/1.57 + iTime*0.1);
    float y = (d-(.5-width))/(2.*width); 
    y = abs(y-.5)*2.;
    vec2 tUv = vec2(x, y);
    col += texture(iChannel0, tUv).rgb *mask;
    col *= 1.-tUv.y;
    //col.rg += id; 
    //if(gv.x > .48 || gv.y > .48) col = vec3(1, 0, 1);//edges

    fragColor = vec4(col,1.0);
}
