void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y; // Normalized pixel coordinates (from 0 to 1)
    vec3 col = vec3(0.);
    float a = uv.x+1.5;
    float b =uv.y-1.5;
    uv.x = -b/(a*a+b*b);
    uv.y = a/(a*a+b*b);
// uv.y= 0.5*cos(sin(uv.y)-3.14/2.);//   uv.x* = 10.*sin(uv.x*2.);
   // uv.y = 20.*tan(uv.y*2.);
    uv *= (2.8*iTime+.230);
    float f = 1.5;
    //uv -= 6.;5
    vec2 id = floor(f*uv); 
    float num = rand(id);

    vec2 gv = fract(f*uv)-0.5;
    if(num<0.5)
        gv.x*=-1.;
    float mask = smoothstep(0.2, 0.,abs(abs(gv.x+gv.y)-0.5));
    col += mask;

    // mask = 15.*sin(gv.x*gv.y);
    //col+=mask;
    //uv.x *=2.;
    fragColor = vec4(col,0.);
}
