vec2 pos = vec2(0,0);
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float zoom = pow(0.999,float(iFrame));
    vec2 normCoord = vec2((fragCoord.x/iResolution.x*4.-2.)*zoom + pos.x, (fragCoord.y/iResolution.y*2.25-1.125)*zoom + pos.y);
    vec2 z = normCoord;
    float zsize;
    int i;
    for(i = 0; i < 150000; i++){
        zsize = dot(z,z);
        z = mat2( z, -z.y,z.x) * z + iMouse.xy / iResolution.xy*4.-2.;
        if(zsize > 4.){
            break;
        }
    }
    if(i == 150000){
    	fragColor = vec4(0,0,0, 0);
    }
    else{

        fragColor = vec4(float(i%137)/137.,float(i%298)/298.,1.-float(i%255)/64.,0);
    }
}
