#define STEPS 45
#define MAX_RENDER 5.0

float freq;

float Sphere(vec3 p, vec4 s)
{
    return length(p-s.xyz)-s.w;
}

vec4 GetDist(vec3 p, int i)
{
    vec4 sphere = vec4(0.5, .1,0., Sphere(p,vec4(0., cos(iTime * .2) *0.5, 3., .8 + (freq*.8))));
    vec4 ground = vec4(0.5, 0.1, 0.6, p.y+.8);
    vec4 group = vec4((sphere.w<ground.w)?sphere.xyz:ground.xyz, min(sphere.w, ground.w));
    return group;
}


vec4 rayMarch(vec3 ro, vec3 rd)
{
    float originD = 0.0;
    vec3 col = vec3(0.0);

 	for(int i = 0; i < STEPS; i++)
    {
        freq = clamp( 2.9*pow( texture( iChannel0, vec2(0.2 + 0.2*float(i)/24.0, 0.15 ) ).x, 3.0 ), 0.0, 1.0 );

        vec3 pos = ro + rd * originD;
        vec4 screenP = GetDist(pos, i);
        originD += screenP.w;
        col += screenP.xyz;

        if(originD > MAX_RENDER)
        {
            col = abs(vec3(float(i)/(freq * 16.)+abs(sin(iTime/8.))*-1.));
        	col += vec3(1.-rd.y * 8.) + vec3(4.2,0.8,3.9);
            break;
        }
    }

    return vec4(col,originD);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    uv.x -= 0.5;
    uv.x *= iResolution.x/iResolution.y;

    uv.y -= 0.5;

    vec3 ro = vec3(0.0,0.0,-.8);
    vec3 rd = normalize(vec3(uv.xy,1.0));

    vec4 c = rayMarch(ro,rd);
    c/=8.;

    vec3 col = vec3(c);
    fragColor = vec4(col,1.0);
}
