void mainImage(out vec4 fragColor, in vec2 fragCoord) {
  vec2 uv = (2.0 * fragCoord.xy - iResolution.xy) / iResolution.xy;
  vec3 c = uv.xyx / dot(uv.xy, uv.xy) * (cos(iTime / 10.0) + 1.0)
         + uv.yxx / dot(uv.xy, uv.xy) * (sin(iTime / 10.0) + 1.0);
  for(int i = 0; i < 10; i++) {
    float t = tan(dot(uv,uv) / 20.0) - 0.35 * sin(dot(c,c)) - 0.35 * cos(dot(c,c));
    c += t / 50.0;
  }
  c += texture(iChannel0,uv).rgb;
  c += tan(c * (sin(iTime / 5.0) + 2.0) * 5.0);
  fragColor = vec4(c, 1.0);
}
