void mainImage( out vec4 fragColor, in vec2 fragCoord )
{    
    float speed = 20.;
    float circles = 30.;
    float force = .005;

    vec2 uv = fragCoord/iResolution.xy;   
    vec2 point = iMouse.xy/iResolution.xy;               

    float d = distance(point, uv);
    float dist = sin(d * circles - iTime * speed);
    vec2 v = (uv - point) / dist;    

    vec3 col = texture(iChannel0, uv + v * force).rgb;

    fragColor = vec4(col, 1.);
}
