#define N 16.0

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.y;
    vec2 UV = fract(uv * N);

    float r = step(0.5, texture(iChannel0, vec3(floor(uv*N)/N, iTime/100.0)).x);
    float tile0 = 1.0 - min(step(0.5, length(UV)), step(0.5, length(1.0 - UV)));
    float tile1 =
        min(step(0.5, length(vec2(1.0 - UV.x, UV.y))), step(0.5, length(vec2(UV.x, 1.0 - UV.y))));

    float h = mix(tile0, tile1, r);
    ivec2 nr = ivec2(floor(uv * N));
    if(nr.x % 2 == nr.y % 2)
        h = 1.0 - h;

    fragColor = vec4(h);
}
