float fn(in vec2 coord){
    float len2 = pow((dot(coord,coord)+(iTime*-.01)),2.);
    return ((sin(len2*1000.)+1.)*.25)+.5;
}

vec2 wobble(in vec2 fc,in float amount,in float rate){
    float r = iTime*rate;
	return fc+(vec2(sin(r),cos(r))*amount);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = ((fragCoord.xy-(iResolution.xy*.5))-.5)/iResolution.x;
    vec2 uvr = wobble(uv,0.05,1.);
    vec2 uvg = wobble(uv,0.04,-0.85);
    vec2 uvb = wobble(uv,0.035,0.89);
    fragColor = vec4(fn(uvr),fn(uvg),fn(uvb),1.);
}
